import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'
import folder from '../assets/folder.svg';
import { useWallet } from '../utils/WalletContext';
import { motion, AnimatePresence  } from 'framer-motion';

const ProjectPage = () => {
    const { contract } = useWallet();
    const [project, setProject] = useState({changes: [], projectName: "", state: -1});
    const { projectCID } = useParams();
    const [copy, setCopy] = useState(false)

    const copyText = () => {
        navigator.clipboard.writeText(projectCID)
        setCopy(true)
        setTimeout(() => {
            setCopy(false)
        }, 1000);
    }

    const getProjectDetails = async () => {
        const projectName = await contract.getProjectName(projectCID);
        const changes = await contract.getProjectChanges(projectCID);
        setProject({...project, projectName: projectName, changes: changes})
    }

    const checkProjectState = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/api/check_project/', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({ projectName: project.projectName, "changes": project.changes }),
            });
        
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    }

    const getProjectFiles = async () => {
      try {
          const response = await fetch('http://127.0.0.1:8000/api/downloadproject/', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ "changes": [1, 2, 3, 4] }),
          });
      
      } catch (error) {
          console.error('Error uploading file:', error);
      }
  }


    useEffect(() => {
        try {
            getProjectDetails()
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    }, []);

    return (
    <div className='background middle center gapLines'>
        <div className='line projectHeaderLine'>
        <AnimatePresence initial={true} mode='wait'>
          <motion.div whileTap={{scale: 0.9}} whileHover={{scale: 1.03}} initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} exit={{scale: .91 }} transition={{ type: "spring", duration: 0.1 }} onClick={() => copyText()} className='projectHeader CIDheader'>
                    <motion.h1 className={copy ? "" : "CIDtext"}>{copy ? "CID copied" : projectCID.slice(0, 15)}</motion.h1>
          </motion.div>
        </AnimatePresence>

        <div className='projectHeader'>
            <h1>{project.projectName}</h1>
        </div>


          <div className='projectHeader'>
            <h1 id='projectName'>{project.projectName}</h1>
          </div>

          <div className='projectHeader'>
            <h1>Up to date</h1>
          </div>
        </div>

        <div className='line projectListFiles'>
                <div className='FileLine'>
                    <span className='FileText'>File name</span>
                    <span className='FileText'>Date</span>
                </div>

                <div className='DirLine'>
                    <span className='FileText'>File name</span>
                    <span className='FileText'>Date</span>
                </div>
        </div>

    </div>
    );
}

export default ProjectPage;
