import React from 'react';
import Editor from "@monaco-editor/react";

const CodeEditor = (text) => {
  return (
    <Editor
        className=''
        width="100%"
        height="100%"
        language="python"
        theme="vs-dark"
        value={text}
    />
  );
}

export default CodeEditor;
