import React from 'react';
import { motion  } from 'framer-motion';

const PageLearn = ({scroll}) => {
  return (
    <div ref={scroll} style={{ overflow: "scroll" }} className=''>
      <motion.h1         
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        viewport={{ root: scroll }}

        variants={{offscreen: {
          y: 300
        },
        onscreen: {
          y: 50,
          rotate: -10,
          transition: {
            type: "spring",
            bounce: 0.4,
            duration: 0.8
          }
        }}}
        className='titleLearn'>
        How it works
      </motion.h1>
    </div>
  );
}

export default PageLearn;
