import { useEffect, useRef } from "react";
import { animate, useScroll, motion } from "framer-motion";

const PageLearn = () => {
  const animControls = useRef();

  useScroll().scrollYProgress.on("change", (yProgress) => {
    if (!animControls.current) return;

    animControls.current.time = yProgress * animControls.current.duration;
  });

  useEffect(() => {
    animControls.current = animate([
      [".ball", { left: 52 }, { ease: "easeOut", duration: 0.4 }],
      [".ball", { left: 70 }, { ease: "easeOut", duration: 1.2, at: 0.401 }],
      [".domino-1", { rotate: 24 }, { duration: 0.4, ease: "easeIn", at: 0.4 }],

      [".domino-1", { rotate: 45 }, { duration: 0.4, ease: "easeIn", at: 0.8 }],
      [
        ".domino-2",
        { rotate: 24 },
        { duration: 0.4, ease: "easeIn", at: 0.801 }
      ],

      [".domino-3", { rotate: 24 }, { duration: 0.4, ease: "easeIn", at: 1.2 }],
      [
        ".domino-2",
        { rotate: 45 },
        { duration: 0.4, ease: "easeIn", at: 1.201 }
      ],
      [
        ".domino-1",
        { rotate: 58 },
        { duration: 0.4, ease: "easeIn", at: 1.201 }
      ],

      [".domino-4", { rotate: 90 }, { duration: 0.4, ease: "easeIn", at: 1.6 }],
      [
        ".domino-3",
        { rotate: 69 },
        { duration: 0.4, ease: "easeIn", at: 1.601 }
      ],
      [
        ".domino-2",
        { rotate: 67 },
        { duration: 0.4, ease: "easeIn", at: 1.601 }
      ],
      [
        ".titleLearn-1",
        { rotate: 67 },
        { duration: 0.4, ease: "easeIn", at: 1.601 }
      ]
    ]);
    animControls.current.pause();
  }, []);


  return (
    <div className=''>
      <motion.h1         
              className="titleLearn"
      >How it works</motion.h1>
    </div>
  );
}

export default PageLearn;
