import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'
import { useWallet } from '../utils/WalletContext';
import { motion, AnimatePresence  } from 'framer-motion';
import HomeSvg from '../assets/home.svg';
import { useNavigate } from 'react-router-dom';
import CodeEditor from '../components/CodeEditor';

const ProjectPage = () => {
    const { contract } = useWallet();
    const navigate = useNavigate()
    const [project, setProject] = useState({changes: [], projectName: "", state: -1, files: [], path: ''});
    const { projectCID } = useParams();
    const [copy, setCopy] = useState(false)
    const [fileContent, setFileContent] = useState(false)

    const copyText = () => {
        navigator.clipboard.writeText(projectCID)
        setCopy(true)
        setTimeout(() => {
            setCopy(false)
        }, 1000);
    }

    function comparePaths(path1, path2) {
      const dir1 = path1.substring(0, path1.lastIndexOf('\\') + 1);
      const dir2 = path2.substring(0, path2.lastIndexOf('\\') + 1);
    
      return dir1 === dir2;
    }

    function getFileNameFromPath(filePath) {
      const pathComponents = filePath.split('\\');
      return pathComponents[pathComponents.length - 1];
    }

    function goBackOneLevel(path) {
      path = path.endsWith('\\') ? path.slice(0, -1) : path;
      const lastIndex = path.lastIndexOf('\\');
      if (lastIndex === -1) {
        return '';
      }
      return path.slice(0, lastIndex + 1);
    }

    const getFilesHtml = () => {
      return project.files.map((fileName, index) => (
        comparePaths(project.path, fileName) ? (
        fileName.includes(".") ? (
            <div onClick={() => {getFileContent(fileName)}} className='FileLine' key={index}>
                <span className='FileText'>{getFileNameFromPath(fileName)}</span>
                <span className='FileText'>Date</span>
            </div>
        ) : (
            <div onClick={() => setProject({...project, path: fileName + "\\"})} className='DirLine' key={index}>
                <span className='FileText'>{getFileNameFromPath(fileName)}</span>
                <span className='FileText'>Date</span>
            </div>
        )) : null
    ))}
    const getProjectDetails = async () => {
      const projectName = await contract.getProjectName(projectCID);
      const changes = await contract.getProjectChanges(projectCID);
      let currentProjectState = {state: -1, projectName: projectName, changes: changes, files: [], path: ''}

      try {
        const response = await fetch('http://127.0.0.1:8000/api/check-project', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ "projectName": currentProjectState.projectName, "changes": currentProjectState.changes })
        });

        const data = await response.json(); 

        if (data["message"] === 353) {
          currentProjectState = {...currentProjectState, state: 353}
        } else if (data["message"] === 354) {
          currentProjectState = {...currentProjectState, state: 354}
        } else if (data["message"] === 351) {
          currentProjectState = {...currentProjectState, state: 351}
        }
        try {
          const response = await fetch('http://127.0.0.1:8000/api/get_project_files', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ "changes": changes }),
          });

          const data = await response.json(); 
          currentProjectState = {...currentProjectState, files: data["files"]}
          
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    
      } catch (error) {
          console.error("Can't get project details", error);
      }
      setProject(currentProjectState)

  }

  const getFileContent = async(file_name) => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/get_file', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ "changes": project.changes, "file_name": file_name }),
      });

      const data = await response.json(); 
      console.log(data["file"])
      setFileContent(data["file"])
      
    } catch (error) {
        console.error('Error uploading file:', error);
    }
  }

  const getState = () => {
    if (project.state === 353) {
      return "Not Downloaded"
    } else if (project.state === 354) {
      return "Latest Version"
    } else if (project.state === 351) {
      return "Need Update"
    }
  }

  useEffect(() => {
      try {
          getProjectDetails()
      } catch (error) {
          console.error('Error uploading file:', error);
      }
  }, []);

    return (
    <div className='background middle center gapLines'>
        <div className='line projectHeaderLine'>
        <AnimatePresence initial={true} mode='wait'>
          <motion.div whileTap={{scale: 0.9}} whileHover={{scale: 1.03}} initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} exit={{scale: .91 }} transition={{ type: "spring", duration: 0.6 }} onClick={() => copyText()} className='projectHeader CIDheader'>
                    <motion.h1 className={copy ? "" : "CIDtext"}>{copy ? "CID copied" : projectCID.slice(0, 15)}</motion.h1>
          </motion.div>
        </AnimatePresence>

        <motion.div whileTap={{scale: 0.9}} whileHover={{scale: 1.03}} initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} exit={{scale: .91 }} transition={{ type: "spring", duration: 0.6 }} onClick={() => {navigate('/')}} className='projectHeader borderPixel HomeButtonDiv'>
            <img className="HomeButton" src={HomeSvg} alt="" />
        </motion.div>


          <motion.div  initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} exit={{scale: .91 }} transition={{ type: "spring", duration: 0.6 }} className='projectHeader'>
            <h1>{project.projectName}</h1>
          </motion.div>

          <motion.div  initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} exit={{scale: .91 }} transition={{ type: "spring", duration: 0.6 }} className='projectHeader'>
            <h1>{getState()}</h1>
          </motion.div>
        </div>

        <div className='line projectListFiles'> 
        {fileContent == false ? <>
        {project.path.length > 1 ? 
        <div onClick={() => {setProject({...project, path: goBackOneLevel(project.path)})}} className='FileLine'>
          <span className='FileText'>..</span>
          <span className='FileText'>Go Back</span>
        </div> : null}
        {getFilesHtml()}
        </> : <>
        {CodeEditor(fileContent)}
        <div onClick={() => {getFileContent(false)}}>Go back</div>
        </>}
        </div>

    </div>
    );
}

export default ProjectPage;
