import React from 'react';
import Editor from "@monaco-editor/react";

const CodeEditor = (text) => {
  return (
    <div className='Editor'>
    <Editor
        width="100%"
        height="100%"
        language="python"
        theme="vs-dark"
        value={text}
    />
    </div>
  );
}

export default CodeEditor;
