import React, {useMemo, useState, useEffect} from 'react';
import { ethers } from 'ethers';
import { CONTRACT_ADDRESS, CONTRACT_ABI } from '../utils/SmartContract';
import { useWallet } from '../utils/WalletContext';
import { motion, AnimatePresence } from 'framer-motion';
import CompleteSvg from '../assets/complete-svgrepo-com.svg';

const CreateNewProject = () => {
    const { account } = useWallet();
    const provider = new ethers.providers.Web3Provider(window.ethereum)
    const signer = provider.getSigner()
    const [projects, setProjects] = useState("");
    const [step, setStep] = useState(0);
    const [name, setName] = useState("");

    const contract = useMemo(() => {
        return new ethers.Contract(CONTRACT_ADDRESS, CONTRACT_ABI, signer)
    }, [signer])

    const createProjectFunction = async () => {
        await contract.createProject("itamare")
    }

    const getProject = async () => {
        try {
            const s = await contract.getProject(0);
            setProjects(s);
        } catch (error) {
            console.error("Error calling getProject:", error);
        }
    };

    const nextStep = () => {
        setStep(step+1)
    }

    const pastePath = async (e) => {
        const text = await navigator.clipboard.readText();
        
    }
    
    const getStep = () => {
        if (step === 0) {
        return (
            <motion.div whileHover={{scale: 1.05}} initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ type: "spring", duration: 0.7 }} onClick={() => nextStep()} className='box-new-project'>
                <h1>Create New Project</h1>
            </motion.div>
        )} else if (step === 1) {
        return (
            <div className='relative-new-project'>
            <motion.div whileHover={{scale: 1.05}} initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ type: "spring", duration: 0.7 }} style={{cursor: "default"}} className='box-new-project'>
                <h1>Project Name</h1>
                <input onChange={(e) => setName(e.target.value)} type="text" className="newProjectInput" />
            </motion.div>
            <AnimatePresence initial={false} mode='wait'>
            {name.length > 3 ? (
            <motion.div key={"divSVG"} exit={{y: -170}} whileHover={{scale: 1.05}} initial={{y: -170 }} animate={{y: 0}} transition={{ type: "spring", duration: 0.7 }} onClick={() => nextStep()} className='button-new-project'>
                <motion.img  transition={{ type: "spring", duration: 1.5 }} initial={{opacity: -1 }} animate={{opacity: 1}} key={"svgSVG"} className='arrowDown' src={CompleteSvg} alt="" />
            </motion.div>
            ) : <></>}
            </AnimatePresence>
            </div>

        )} else if (step === 2) {
        return (
            <motion.div style={{cursor: "default"}} whileHover={{scale: 1.05}} initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ type: "spring", duration: 0.7 }} className='box-new-project'>
                <h1>Project Initial Folder</h1>
                {step === 2 ?
                <div onClick={() => {}} style={{cursor: "pointer"}} className="newProjectInput">
                    <span className='pastePathButton'>Paste Project Path</span>
                </div>
                :<input type="text" className="newProjectInput" />}
            </motion.div>
        )}
    }

    useEffect(() => {

    }, []);

    return (
    <div className='background center'>
        {getStep()}
    </div>
    );
}

export default CreateNewProject;
