import { motion } from 'framer-motion';
import './App.css';
import downArrow from './assets/down-arrow-svgrepo-com.svg';
import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';

function Home() {
  const [connected, setConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState("");

  useEffect(() => {
    async function connectWallet() {
      if (!connected) {
        // Connect the wallet using ethers.js
        const provider = new ethers.BrowserProvider(window.ethereum);
        const signer = await provider.getSigner();
        const _walletAddress = await signer.getAddress();
        setConnected(true);
        setWalletAddress(_walletAddress);
      } else {
        // Disconnect the wallet
        window.ethereum.selectedAddress = null;
        setConnected(false);
        setWalletAddress("");
      }
    }
  }, []);

  return (
    <div className="background center">
      <div className='line'>
        <div className='box-search'>
          <h1 className='Title'>Search</h1>
          <input className='search' type="text"/>
        </div>
      </div>

      <div className='line'>
        <div className='box-projects'>
          <h1 className='Title'>Popular Projects</h1>
          <motion.div whileHover={{scale: 1.05}} initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ type: "spring", duration: 0.7 }} className='project-box'>
            <span className='project-box-header'>Project name</span>
            <span>creation time | participants</span>
          </motion.div>
        </div>

        <div>
          <motion.div whileHover={{scale: 1.03}} animate={{ scale: 1 }} transition={{ type: "spring", duration: 0.7 }} className='box-create-project'>
            <h1 className='Title TitleCreatePorject'>Create <br /> New Project</h1>
          </motion.div>

          <motion.div whileHover={{scale: 1.03}} animate={{ scale: 1 }} transition={{ type: "spring", duration: 0.7 }} className='box-arrow-down'>
            <img className='arrowDown' src={downArrow} alt="" />
          </motion.div>
        </div>

        <div className='box-projects'>
          <h1 className='Title'>My Projects</h1>

          <motion.div whileHover={{scale: 1.05}} initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ type: "spring", duration: 0.7 }} className='project-box'>
            <span className='project-box-header'>Project name</span>
            <span>creation time | participants</span>
          </motion.div>

        </div>
      </div>

    </div>
  );
}

export default Home;
